CREATE TABLE IF NOT EXISTS whatsapp.messages (
    message_id uuid,
    message_content text, 
    sent_at timeuuid,
    group_id uuid,
    sender uuid,
    message_reply uuid,
    PRIMARY KEY ((group_id, message_id), sent_at)
);
DESCRIBE TABLE whatsapp.messages;

CREATE TABLE IF NOT EXISTS whatsapp.groups(
    group_id uuid,
    created_at timeuuid,
    admin uuid,
    participants set<uuid>,
    PRIMARY KEY (admin, created_at)
);
DESCRIBE TABLE whatsapp.groups;

